import React, { useState } from 'react';
import './App.css';

const App = () => {
	const [index, setIndex] = useState(0);

	const phrases = [
		'Tu primera frase aquí',
		'Tu segunda frase aquí',
		'Tu tercera frase aquí',
		'Tu cuarta frase aquí',
	];

	const images = ['/image1.jpg', '/image2.jpg', '/image3.jpg', '/image4.jpg'];

	const handleClick = () => {
		const newIndex = (index + 1) % phrases.length;
		setIndex(newIndex);
	};

	return (
		<div className="App">
			<div
				className="App-background"
				style={{ backgroundImage: `url(${images[index]})` }}
			>
				<div className="App-content">
					<div className="App-phrase-box">
						<p className="App-phrase">{phrases[index]}</p>
					</div>
					<button onClick={handleClick}>Cambiar frase e imagen</button>
				</div>
			</div>
		</div>
	);
};

export default App;
