import React, { useState, useEffect } from 'react';
import './App.css';
import backgroundMusic from './background-music.mp3'; // Asegúrate de tener este archivo en tu proyecto

const App = () => {
	const [index, setIndex] = useState(0);
	const [clickCount, setClickCount] = useState(0);
	const [dateTime, setDateTime] = useState(new Date());

	const phrases = [
		'No todo el mundo puede recibir las mismas cosas. Se practico.',
		'Alégrate, un camino de hermosas pasiones te espera y debes transitarlo',
		'No olvides que un amigo es un regalo que te das a ti mismo.',
		'No te tomes la vida demasiado en serio. No saldrás de ella con vida.',
	];

	const images = ['/image1.jpg', '/image2.jpg', '/image3.jpg', '/image4.jpg'];

	const handleClick = () => {
		const newIndex = (index + 1) % phrases.length;
		setIndex(newIndex);
		setClickCount(clickCount + 1);
	};

	useEffect(() => {
		const timer = setInterval(() => {
			setDateTime(new Date());
		}, 1000);
		return () => clearInterval(timer);
	}, []);

	return (
		<div className="App">
			<header className="App-header">
				<h1>Galleta De La Fortuna</h1>
				<img src={images[index]} className="App-logo" alt="fortune cookie" />
				<p className="App-phrase">{phrases[index]}</p>
				<button onClick={handleClick}>Tu Fortuna</button>
				<p>{dateTime.toLocaleString()}</p>
				<p>Clicks: {clickCount}</p>
				<audio src={backgroundMusic} autoPlay loop />
			</header>
		</div>
	);
};

export default App;
