import React, { useState } from 'react';
import './App.css';

const App = () => {
	const [index, setIndex] = useState(0);

	const phrases = [
		'Tu primera frase aquí',
		'Tu segunda frase aquí',
		'Tu tercera frase aquí',
		'Tu cuarta frase aquí',
	];

	const images = ['/image1.jpg', '/image2.jpg', '/image3.jpg', '/image4.jpg'];

	const handleClick = () => {
		const newIndex = (index + 1) % phrases.length;
		setIndex(newIndex);
	};

	return (
		<div className="App">
			<header className="App-header">
				<img src={images[index]} className="App-logo" alt="fortune cookie" />
				<p className="App-phrase">{phrases[index]}</p>
				<button onClick={handleClick}>Nueva Frase </button>
			</header>
		</div>
	);
};

export default App;
